import React, { useState } from 'react';

const GroupManager = ({ groups, socket, roomId, currentPdf, user }) => {
    const [newGroupName, setNewGroupName] = useState("");
    const userId = user.id || user._id;

    const createGroup = () => {
        if (!newGroupName.trim()) return;
        socket.emit("create-group", {
            roomId,
            groupName: newGroupName,
            permissions: { canDraw: true, canNavigate: true },
            userId // Send creator ID
        });
        setNewGroupName("");
    };

    const deleteGroup = (groupId) => {
        socket.emit("delete-group", { roomId, groupId });
    };

    const joinGroupRequest = (groupId) => {
        socket.emit("request-join-group", { roomId, groupId, userId, userName: user.nombre });
    };

    const approveRequest = (groupId, targetUserId) => {
        socket.emit("approve-join-request", { roomId, groupId, targetUserId });
    };

    const rejectRequest = (groupId, targetUserId) => {
        socket.emit("reject-join-request", { roomId, groupId, targetUserId });
    };

    const leaveGroup = (groupId) => {
        socket.emit("remove-group-member", { roomId, groupId, memberId: userId });
    };

    const linkPdf = (groupId) => {
        socket.emit("link-pdf-group", { roomId, groupId });
    };

    return (
        <div style={{
            position: 'absolute', top: '50px', right: '10px',
            background: 'white', padding: '15px', borderRadius: '8px',
            boxShadow: '0 4px 6px rgba(0,0,0,0.1)', zIndex: 1000,
            width: '300px', maxHeight: '80vh', overflowY: 'auto'
        }}>
            <h3>Gestión de Grupos</h3>

            <div style={{ marginBottom: '15px', display: 'flex' }}>
                <input
                    value={newGroupName}
                    onChange={(e) => setNewGroupName(e.target.value)}
                    placeholder="Nuevo grupo..."
                    style={{ flex: 1, padding: '5px' }}
                />
                <button onClick={createGroup} style={{ marginLeft: '5px' }}>+</button>
            </div>

            <div>
                {groups.map(g => {
                    const isCreator = g.creatorId === userId;
                    const isMember = g.members.includes(userId);
                    const isPending = g.requests && g.requests.some(r => r.userId === userId);

                    return (
                        <div key={g._id} style={{ border: '1px solid #eee', padding: '10px', marginBottom: '10px', borderRadius: '4px' }}>
                            <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
                                <strong>{g.name}</strong>
                                {isCreator && (
                                    <button onClick={() => deleteGroup(g._id)} style={{ color: 'red' }}>×</button>
                                )}
                            </div>

                            <div style={{ fontSize: '0.8em', color: '#666', margin: '5px 0' }}>
                                {g.members.length} miembros
                            </div>

                            {isCreator && g.requests && g.requests.length > 0 && (
                                <div style={{ background: '#fff3cd', padding: '5px', marginBottom: '5px' }}>
                                    <strong>Solicitudes:</strong>
                                    {g.requests.map(r => (
                                        <div key={r.userId} style={{ display: 'flex', justifyContent: 'space-between' }}>
                                            <span>{r.userName}</span>
                                            <div>
                                                <button onClick={() => approveRequest(g._id, r.userId)}>✓</button>
                                                <button onClick={() => rejectRequest(g._id, r.userId)}>✗</button>
                                            </div>
                                        </div>
                                    ))}
                                </div>
                            )}

                            <div style={{ display: 'flex', gap: '5px', marginTop: '5px' }}>
                                {isMember ? (
                                    <button onClick={() => leaveGroup(g._id)}>Salir</button>
                                ) : isPending ? (
                                    <span style={{ color: 'orange' }}>Pendiente...</span>
                                ) : (
                                    <button onClick={() => joinGroupRequest(g._id)}>Solicitar Unirse</button>
                                )}

                                {(isCreator || isMember) && (
                                    <button onClick={() => linkPdf(g._id)} disabled={currentPdf?.linkedGroupId === g._id}>
                                        {currentPdf?.linkedGroupId === g._id ? 'Vinculado' : 'Vincular PDF'}
                                    </button>
                                )}
                            </div>
                        </div>
                    );
                })}
            </div>
        </div>
    );
};

export default GroupManager;
