import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

function Home() {
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

  const createRoom = async () => {
    setLoading(true);
    try {
      // Use window.location.hostname to allow local network access
      const protocol = window.location.protocol;
      const host = window.location.hostname;
      const backendUrl = `${protocol}//${host}:5000/api/rooms`;

      const response = await axios.post(backendUrl, {
        creatorId: 1 // hardcoded for MVP
      });
      const { room_id } = response.data;
      navigate(`/room/${room_id}`);
    } catch (error) {
      console.error("Error creating room:", error);
      alert("Error generating room. Is the backend server running on port 5000?");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="fade-in" style={{ display: 'flex', flexDirection: 'column', alignItems: 'center', justifyContent: 'center', height: '100vh' }}>
      <div className="card" style={{ maxWidth: '500px', width: '90%', textAlign: 'center' }}>
        <h1 style={{ marginBottom: '1rem', fontSize: '2.5rem', background: 'linear-gradient(to right, #60a5fa, #a855f7)', WebkitBackgroundClip: 'text', WebkitTextFillColor: 'transparent' }}>
          Videoconferencia
        </h1>
        <p style={{ color: 'var(--text-secondary)', marginBottom: '2rem', lineHeight: '1.6' }}>
          Conecta con tus amigos y colegas de forma segura y rápida. Crea una sala y comparte el enlace para empezar.
        </p>
        <button
          className="btn"
          onClick={createRoom}
          disabled={loading}
          style={{ width: '100%', fontSize: '1.1rem' }}
        >
          {loading ? 'Creando Sala...' : 'Nueva Reunión'}
        </button>
      </div>
    </div>
  );
}

export default Home;
