import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

function Home() {
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

  const createRoom = async () => {
    setLoading(true);
    try {
      // Allow connection to backend
      const response = await axios.post('http://localhost:5000/api/rooms', {
        creatorId: 1 // hardcoded for MVP
      });
      const { room_id } = response.data;
      navigate(`/room/${room_id}`);
    } catch (error) {
      console.error("Error creating room:", error);
      alert("Error generating room. Is backend running?");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div style={{ textAlign: 'center', marginTop: '50px' }}>
      <h1>Videoconference App</h1>
      <p>Simple WebRTC + Socket.io Implementation</p>
      <button
        onClick={createRoom}
        disabled={loading}
        style={{ padding: '10px 20px', fontSize: '1.2rem', cursor: 'pointer' }}
      >
        {loading ? 'Creating...' : 'Create New Room'}
      </button>
    </div>
  );
}

export default Home;
