import React, { useEffect, useRef, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import Peer from "simple-peer";
import io from "socket.io-client";
import { useAuth } from "../context/AuthContext";
import { useAudioLevel } from "../hooks/useAudioLevel";
import axios from "axios";

function Room() {
  const { roomId } = useParams();
  const { user } = useAuth();
  const navigate = useNavigate();

  // Media streams
  const [myStream, setMyStream] = useState(null);
  const [peers, setPeers] = useState([]);
  const [screenStream, setScreenStream] = useState(null);

  // UI state
  const [audioEnabled, setAudioEnabled] = useState(true);
  const [videoEnabled, setVideoEnabled] = useState(true);
  const [showChat, setShowChat] = useState(true);
  const [showPdf, setShowPdf] = useState(false);

  // Chat
  const [messages, setMessages] = useState([]);
  const [newMessage, setNewMessage] = useState("");

  // PDF
  const [pdfState, setPdfState] = useState(null);
  const [uploadingPdf, setUploadingPdf] = useState(false);

  // Active speakers
  const [activeSpeakers, setActiveSpeakers] = useState(new Set());

  // Refs
  const myVideo = useRef();
  const socketRef = useRef();
  const peersRef = useRef([]);
  const chatEndRef = useRef();
  const fileInputRef = useRef();

  const host = window.location.hostname;
  const API_URL = '/api';

  // Detect if I'm speaking
  const isSpeaking = useAudioLevel(myStream);

  useEffect(() => {
    if (isSpeaking) {
      socketRef.current?.emit("user-speaking", {
        roomId,
        userId: user.id,
        userName: user.nombre
      });
    } else {
      socketRef.current?.emit("user-stopped-speaking", {
        roomId,
        userId: user.id
      });
    }
  }, [isSpeaking]);

  useEffect(() => {
    // Check if mediaDevices is available (requires HTTPS or localhost)
    if (!navigator.mediaDevices || !navigator.mediaDevices.getUserMedia) {
      alert(
        "âš ï¸ WebRTC no estÃ¡ disponible.\n\n" +
        "Para usar video/audio, debes acceder desde:\n" +
        "â€¢ http://localhost:3000 (en este equipo)\n" +
        "â€¢ https://... (conexiÃ³n segura)\n\n" +
        "Actualmente estÃ¡s en: " + window.location.href + "\n\n" +
        "El chat y PDF funcionarÃ¡n, pero no el video."
      );
      // Continue without video/audio
      socketRef.current = io.connect(window.location.origin);
      const socket = socketRef.current;

      socket.emit("join-room", {
        roomId,
        userId: user.id,
        userName: user.nombre
      });

      loadChatHistory();
      return;
    }

    // Initialize socket
    socketRef.current = io.connect(window.location.origin);
    const socket = socketRef.current;

    // Get user media
    navigator.mediaDevices
      .getUserMedia({ video: true, audio: true })
      .then((currentStream) => {
        setMyStream(currentStream);
        if (myVideo.current) {
          myVideo.current.srcObject = currentStream;
        }

        // Join room
        socket.emit("join-room", {
          roomId,
          userId: user.id,
          userName: user.nombre
        });

        // Load chat history
        loadChatHistory();

        // Socket event listeners (AFTER stream is ready)
        socket.on("room-participants", (participants) => {
          console.log("Current participants:", participants);
          // Create peers for existing participants
          participants.forEach((participant) => {
            if (participant.socketId !== socket.id && participant.socketId) {
              console.log("Creating peer for existing participant:", participant.socketId);
              createPeer(participant.socketId, socket.id, currentStream);
            }
          });
        });

        socket.on("user-joined", ({ userId: newUserId, userName, participants }) => {
          console.log(`${userName} joined`);
          // The new user will initiate the connection, so we don't need to do anything here
        });
      })
      .catch((err) => {
        console.error("Error accessing media devices:", err);
        alert(
          "No se pudo acceder a la cÃ¡mara o micrÃ³fono.\n\n" +
          "Posibles causas:\n" +
          "â€¢ Permisos denegados\n" +
          "â€¢ CÃ¡mara/micrÃ³fono en uso por otra app\n" +
          "â€¢ Dispositivos no disponibles\n\n" +
          "El chat y PDF funcionarÃ¡n normalmente."
        );

        // Continue without video/audio
        socket.emit("join-room", {
          roomId,
          userId: user.id,
          userName: user.nombre
        });

        loadChatHistory();
      });

    // Socket event listeners (for offers/answers from other peers)
    socket.on("offer", ({ offer, from }) => {
      console.log("Received offer from:", from);
      if (myStream) {
        const peer = addPeer(offer, from, myStream);
        peersRef.current.push({
          peerID: from,
          peer
        });
        setPeers([...peersRef.current]);
      } else {
        console.warn("Cannot accept offer, no stream available");
      }
    });

    socket.on("answer", ({ answer, from }) => {
      console.log("Received answer from:", from);
      const item = peersRef.current.find((p) => p.peerID === from);
      if (item) {
        item.peer.signal(answer);
      }
    });

    socket.on("ice-candidate", ({ candidate, from }) => {
      const item = peersRef.current.find((p) => p.peerID === from);
      if (item) {
        item.peer.signal(candidate);
      }
    });

    socket.on("user-left", ({ socketId, participants }) => {
      const peerObj = peersRef.current.find((p) => p.peerID === socketId);
      if (peerObj) {
        peerObj.peer.destroy();
      }
      peersRef.current = peersRef.current.filter((p) => p.peerID !== socketId);
      setPeers([...peersRef.current]);

      setActiveSpeakers(prev => {
        const newSet = new Set(prev);
        newSet.delete(socketId);
        return newSet;
      });
    });

    // Chat events
    socket.on("receive-message", (message) => {
      setMessages((prev) => [...prev, message]);
      setTimeout(() => chatEndRef.current?.scrollIntoView({ behavior: "smooth" }), 100);
    });

    // PDF events
    socket.on("pdf-state", (pdfData) => {
      setPdfState(pdfData);
      setShowPdf(true);
    });

    socket.on("pdf-page-update", ({ currentPage }) => {
      setPdfState((prev) => prev ? { ...prev, currentPage } : null);
    });

    socket.on("pdf-removed", () => {
      setPdfState(null);
      setShowPdf(false);
    });

    // Active speaker events
    socket.on("user-speaking", ({ socketId }) => {
      setActiveSpeakers(prev => new Set(prev).add(socketId));
    });

    socket.on("user-stopped-speaking", ({ socketId }) => {
      setActiveSpeakers(prev => {
        const newSet = new Set(prev);
        newSet.delete(socketId);
        return newSet;
      });
    });

    // Screen sharing events
    socket.on("screen-share-active", ({ socketId }) => {
      console.log("Screen share started by:", socketId);
    });

    socket.on("screen-share-ended", () => {
      console.log("Screen share ended");
    });

    // Cleanup
    return () => {
      if (myStream) {
        myStream.getTracks().forEach((track) => track.stop());
      }
      if (screenStream) {
        screenStream.getTracks().forEach((track) => track.stop());
      }
      peersRef.current.forEach(({ peer }) => peer.destroy());
      socket.disconnect();
    };
  }, [roomId]);

  function createPeer(userToSignal, callerID, stream) {
    const peer = new Peer({
      initiator: true,
      trickle: false,
      stream,
      config: peerConfig
    });

    peer.on("signal", (signal) => {
      console.log("Sending signal (offer) to:", userToSignal);
      socketRef.current.emit("offer", {
        userToSignal,
        callerID,
        signal
      });
    });

    peer.on("stream", (remoteStream) => {
      // Stream will be handled in the peer object
    });

    peer.on("error", (err) => {
      console.error("Peer error (initiator):", err);
    });

    peersRef.current.push({
      peerID: userToSignal,
      peer
    });
    setPeers([...peersRef.current]);

    return peer;
  }

  function addPeer(incomingSignal, callerID, stream) {
    console.log("Creating peer (receiver) from:", callerID);
    const peer = new Peer({
      initiator: false,
      trickle: false,
      stream,
      config: peerConfig
    });

    peer.on("signal", (signal) => {
      console.log("Sending signal (answer) to:", callerID);
      socketRef.current.emit("answer", {
        signal,
        callerID
      });
    });

    peer.on("error", (err) => {
      console.error("Peer error (receiver):", err);
    });

    peer.signal(incomingSignal);

    return peer;
  }

  const loadChatHistory = async () => {
    try {
      const response = await axios.get(`${API_URL}/chat/${roomId}`);
      setMessages(response.data.messages);
      setTimeout(() => chatEndRef.current?.scrollIntoView(), 100);
    } catch (error) {
      console.error("Error loading chat:", error);
    }
  };

  const sendMessage = (e) => {
    e.preventDefault();
    if (newMessage.trim()) {
      const messageData = {
        roomId,
        userId: user.id, // Ensure this uses the current user ID
        userName: user.nombre,
        texto: newMessage,
        timestamp: new Date()
      };

      // Optimistic update
      setMessages((prev) => [...prev, messageData]);

      socketRef.current.emit("send-message", messageData);
      setNewMessage("");
    }
  };

  const toggleAudio = () => {
    if (myStream) {
      const audioTrack = myStream.getAudioTracks()[0];
      audioTrack.enabled = !audioTrack.enabled;
      setAudioEnabled(audioTrack.enabled);

      socketRef.current.emit("toggle-audio", {
        roomId,
        userId: user.id,
        audioEnabled: audioTrack.enabled
      });
    }
  };

  const toggleVideo = () => {
    if (myStream) {
      const videoTrack = myStream.getVideoTracks()[0];
      videoTrack.enabled = !videoTrack.enabled;
      setVideoEnabled(videoTrack.enabled);

      socketRef.current.emit("toggle-video", {
        roomId,
        userId: user.id,
        videoEnabled: videoTrack.enabled
      });
    }
  };

  const handlePdfUpload = async (e) => {
    const file = e.target.files[0];
    if (!file || file.type !== "application/pdf") {
      alert("Por favor selecciona un archivo PDF");
      return;
    }

    setUploadingPdf(true);
    const formData = new FormData();
    formData.append("pdf", file);
    formData.append("totalPages", "1"); // Will be updated by backend if needed

    try {
      const response = await axios.post(
        `${API_URL}/pdf/upload/${roomId}`,
        formData,
        {
          headers: { "Content-Type": "multipart/form-data" }
        }
      );

      const pdfData = response.data.pdf;
      socketRef.current.emit("pdf-uploaded", { roomId, pdfData });
      setPdfState(pdfData);
      setShowPdf(true);
    } catch (error) {
      console.error("Error uploading PDF:", error);
      alert("Error al subir PDF");
    } finally {
      setUploadingPdf(false);
    }
  };

  const changePdfPage = (direction) => {
    if (!pdfState) return;

    let newPage = pdfState.currentPage + direction;
    if (newPage < 1) newPage = 1;
    if (newPage > pdfState.totalPages) newPage = pdfState.totalPages;

    socketRef.current.emit("pdf-page-changed", { roomId, currentPage: newPage });
    setPdfState({ ...pdfState, currentPage: newPage });
  };

  const shareScreen = async () => {
    if (!navigator.mediaDevices || !navigator.mediaDevices.getDisplayMedia) {
      alert("Compartir pantalla requiere HTTPS o localhost");
      return;
    }

    try {
      const stream = await navigator.mediaDevices.getDisplayMedia({
        video: true,
        audio: false
      });

      setScreenStream(stream);
      socketRef.current.emit("screen-share-started", {
        roomId,
        userId: user.id
      });

      stream.getVideoTracks()[0].onended = () => {
        stopScreenShare();
      };
    } catch (error) {
      console.error("Error sharing screen:", error);
      if (error.name === 'NotAllowedError') {
        alert("Permiso denegado para compartir pantalla");
      }
    }
  };

  const stopScreenShare = () => {
    if (screenStream) {
      screenStream.getTracks().forEach((track) => track.stop());
      setScreenStream(null);
      socketRef.current.emit("screen-share-stopped", { roomId });
    }
  };

  const leaveRoom = () => {
    if (myStream) {
      myStream.getTracks().forEach((track) => track.stop());
    }
    navigate("/");
  };

  return (
    <div style={{ display: "flex", height: "100vh", background: "var(--bg-primary)" }}>
      {/* Main video area */}
      <div style={{ flex: 1, display: "flex", flexDirection: "column", padding: "20px" }}>
        {/* Header */}
        <div style={{ marginBottom: "20px", display: "flex", justifyContent: "space-between", alignItems: "center" }}>
          <h2 style={{ color: "var(--text-secondary)", margin: 0 }}>
            Sala: <span style={{ color: "var(--accent-primary)" }}>{roomId}</span>
          </h2>
          <div style={{ display: "flex", gap: "10px" }}>
            <button
              onClick={() => setShowChat(!showChat)}
              className="btn"
              style={{ padding: "8px 16px", fontSize: "0.9rem" }}
            >
              {showChat ? "Ocultar" : "Mostrar"} Chat
            </button>
            <button
              onClick={() => setShowPdf(!showPdf)}
              className="btn"
              style={{ padding: "8px 16px", fontSize: "0.9rem" }}
            >
              {showPdf ? "Ocultar" : "Mostrar"} PDF
            </button>
          </div>
        </div>

        {/* Video grid */}
        <div style={{
          flex: 1,
          display: "grid",
          gridTemplateColumns:
            peers.length === 0 ? "1fr" :
              peers.length === 1 ? "repeat(2, 1fr)" :
                peers.length <= 4 ? "repeat(2, 1fr)" :
                  peers.length <= 9 ? "repeat(3, 1fr)" :
                    "repeat(4, 1fr)",
          gap: "10px",
          marginBottom: "20px",
          overflow: "auto",
          padding: "10px",
          alignContent: "start"
        }}>
          {/* My video */}
          <div style={{
            position: "relative",
            background: "#1a1a1a",
            borderRadius: "12px",
            overflow: "hidden",
            border: isSpeaking ? "3px solid #10b981" : "3px solid transparent",
            transition: "all 0.3s ease",
            boxShadow: isSpeaking ? "0 0 20px rgba(16, 185, 129, 0.5)" : "0 4px 6px rgba(0,0,0,0.3)",
            aspectRatio: "16/9",
            minHeight: "200px"
          }}>
            <video
              playsInline
              muted
              ref={myVideo}
              autoPlay
              style={{
                width: "100%",
                height: "100%",
                objectFit: "cover",
                display: videoEnabled ? "block" : "none"
              }}
            />
            {!videoEnabled && (
              <div style={{
                width: "100%",
                height: "100%",
                display: "flex",
                alignItems: "center",
                justifyContent: "center",
                background: "linear-gradient(135deg, #667eea 0%, #764ba2 100%)"
              }}>
                <div style={{
                  width: "80px",
                  height: "80px",
                  borderRadius: "50%",
                  background: "rgba(255,255,255,0.2)",
                  display: "flex",
                  alignItems: "center",
                  justifyContent: "center",
                  fontSize: "2rem",
                  color: "white",
                  fontWeight: "bold"
                }}>
                  {user?.nombre?.charAt(0).toUpperCase()}
                </div>
              </div>
            )}
            <div style={{
              position: "absolute",
              bottom: "10px",
              left: "10px",
              background: "rgba(0,0,0,0.8)",
              padding: "6px 12px",
              borderRadius: "6px",
              color: "white",
              fontSize: "0.9rem",
              fontWeight: "500",
              display: "flex",
              alignItems: "center",
              gap: "6px"
            }}>
              {!audioEnabled && <span style={{ color: "#ef4444" }}>ðŸ”‡</span>}
              {user?.nombre} (TÃº)
            </div>
            {isSpeaking && (
              <div style={{
                position: "absolute",
                top: "10px",
                right: "10px",
                background: "rgba(16, 185, 129, 0.9)",
                padding: "4px 8px",
                borderRadius: "4px",
                color: "white",
                fontSize: "0.75rem",
                fontWeight: "600"
              }}>
                ðŸŽ¤ Hablando
              </div>
            )}
          </div>

          {/* Peer videos */}
          {peers.map(({ peerID, peer }) => (
            <VideoCard
              key={peerID}
              peer={peer}
              peerID={peerID}
              isActive={activeSpeakers.has(peerID)}
            />
          ))}
        </div>

        {/* Control bar */}
        <div style={{
          display: "flex",
          justifyContent: "center",
          gap: "15px",
          padding: "15px",
          background: "rgba(255,255,255,0.05)",
          borderRadius: "12px"
        }}>
          <button
            onClick={toggleAudio}
            style={{
              padding: "12px 20px",
              borderRadius: "8px",
              border: "none",
              background: audioEnabled ? "var(--accent-primary)" : "#ef4444",
              color: "white",
              cursor: "pointer",
              fontSize: "1rem",
              fontWeight: "500"
            }}
          >
            {audioEnabled ? "ðŸŽ¤ MicrÃ³fono" : "ðŸ”‡ Mutear"}
          </button>
          <button
            onClick={toggleVideo}
            style={{
              padding: "12px 20px",
              borderRadius: "8px",
              border: "none",
              background: videoEnabled ? "var(--accent-primary)" : "#ef4444",
              color: "white",
              cursor: "pointer",
              fontSize: "1rem",
              fontWeight: "500"
            }}
          >
            {videoEnabled ? "ðŸ“¹ CÃ¡mara" : "ðŸ“· Sin Video"}
          </button>
          <button
            onClick={screenStream ? stopScreenShare : shareScreen}
            style={{
              padding: "12px 20px",
              borderRadius: "8px",
              border: "none",
              background: screenStream ? "#ef4444" : "var(--accent-secondary)",
              color: "white",
              cursor: "pointer",
              fontSize: "1rem",
              fontWeight: "500"
            }}
          >
            {screenStream ? "â¹ï¸ Detener" : "ðŸ–¥ï¸ Compartir Pantalla"}
          </button>
          <button
            onClick={() => fileInputRef.current?.click()}
            disabled={uploadingPdf}
            style={{
              padding: "12px 20px",
              borderRadius: "8px",
              border: "none",
              background: "#8b5cf6",
              color: "white",
              cursor: uploadingPdf ? "not-allowed" : "pointer",
              fontSize: "1rem",
              fontWeight: "500"
            }}
          >
            {uploadingPdf ? "Subiendo..." : "ðŸ“„ Subir PDF"}
          </button>
          <input
            ref={fileInputRef}
            type="file"
            accept="application/pdf"
            onChange={handlePdfUpload}
            style={{ display: "none" }}
          />
          <button
            onClick={leaveRoom}
            style={{
              padding: "12px 20px",
              borderRadius: "8px",
              border: "none",
              background: "#dc2626",
              color: "white",
              cursor: "pointer",
              fontSize: "1rem",
              fontWeight: "500"
            }}
          >
            ðŸšª Salir
          </button>
        </div>
      </div>

      {/* Chat panel */}
      {showChat && (
        <div style={{
          width: "350px",
          background: "rgba(30, 30, 40, 0.95)",
          borderLeft: "1px solid rgba(255,255,255,0.1)",
          display: "flex",
          flexDirection: "column",
          boxShadow: "-4px 0 10px rgba(0,0,0,0.3)"
        }}>
          <div style={{
            padding: "20px",
            borderBottom: "1px solid rgba(255,255,255,0.1)",
            background: "rgba(0,0,0,0.2)"
          }}>
            <h3 style={{
              margin: 0,
              color: "var(--text-primary)",
              fontSize: "1.1rem",
              fontWeight: "600"
            }}>
              ðŸ’¬ Chat
            </h3>
            <p style={{
              margin: "5px 0 0 0",
              fontSize: "0.75rem",
              color: "var(--text-secondary)"
            }}>
              {messages.length} mensaje{messages.length !== 1 ? 's' : ''}
            </p>
          </div>
          <div style={{
            flex: 1,
            overflowY: "auto",
            padding: "15px",
            display: "flex",
            flexDirection: "column",
            gap: "12px"
          }}>
            {messages.length === 0 ? (
              <div style={{
                textAlign: "center",
                color: "var(--text-secondary)",
                padding: "40px 20px",
                fontSize: "0.9rem"
              }}>
                <div style={{ fontSize: "2rem", marginBottom: "10px" }}>ðŸ’¬</div>
                No hay mensajes aÃºn.<br />
                Â¡SÃ© el primero en escribir!
              </div>
            ) : (
              messages.map((msg, idx) => {
                const isMe = msg.userId === user.id;
                const time = new Date(msg.timestamp).toLocaleTimeString('es-ES', {
                  hour: '2-digit',
                  minute: '2-digit'
                });

                return (
                  <div key={idx} style={{
                    display: "flex",
                    flexDirection: "column",
                    alignItems: isMe ? "flex-end" : "flex-start",
                    animation: "fadeIn 0.3s ease"
                  }}>
                    {!isMe && (
                      <div style={{
                        fontSize: "0.75rem",
                        color: "var(--accent-primary)",
                        marginBottom: "3px",
                        fontWeight: "600"
                      }}>
                        {msg.userName}
                      </div>
                    )}
                    <div style={{
                      background: isMe
                        ? "linear-gradient(135deg, #667eea 0%, #764ba2 100%)"
                        : "rgba(255,255,255,0.08)",
                      padding: "10px 14px",
                      borderRadius: isMe ? "12px 12px 4px 12px" : "12px 12px 12px 4px",
                      maxWidth: "80%",
                      wordWrap: "break-word",
                      boxShadow: "0 2px 4px rgba(0,0,0,0.2)"
                    }}>
                      <div style={{
                        color: "var(--text-primary)",
                        fontSize: "0.95rem",
                        lineHeight: "1.4"
                      }}>
                        {msg.texto}
                      </div>
                      <div style={{
                        fontSize: "0.7rem",
                        color: isMe ? "rgba(255,255,255,0.7)" : "var(--text-secondary)",
                        marginTop: "4px",
                        textAlign: "right"
                      }}>
                        {time}
                      </div>
                    </div>
                  </div>
                );
              })
            )}
            <div ref={chatEndRef} />
          </div>
          <form onSubmit={sendMessage} style={{
            padding: "15px",
            borderTop: "1px solid rgba(255,255,255,0.1)",
            display: "flex",
            gap: "10px",
            background: "rgba(0,0,0,0.2)"
          }}>
            <input
              type="text"
              value={newMessage}
              onChange={(e) => setNewMessage(e.target.value)}
              placeholder="Escribe un mensaje..."
              style={{
                flex: 1,
                padding: "12px",
                borderRadius: "8px",
                border: "1px solid rgba(255,255,255,0.1)",
                background: "rgba(255,255,255,0.05)",
                color: "var(--text-primary)",
                fontSize: "0.95rem",
                outline: "none",
                transition: "all 0.2s"
              }}
              onFocus={(e) => e.target.style.borderColor = "var(--accent-primary)"}
              onBlur={(e) => e.target.style.borderColor = "rgba(255,255,255,0.1)"}
            />
            <button
              type="submit"
              className="btn"
              style={{
                padding: "12px 20px",
                minWidth: "70px",
                fontSize: "0.9rem"
              }}
              disabled={!newMessage.trim()}
            >
              Enviar
            </button>
          </form>
        </div>
      )}

      {/* PDF panel */}
      {showPdf && pdfState && (
        <div style={{
          width: "400px",
          background: "rgba(255,255,255,0.03)",
          borderLeft: "1px solid rgba(255,255,255,0.1)",
          display: "flex",
          flexDirection: "column"
        }}>
          <div style={{
            padding: "20px",
            borderBottom: "1px solid rgba(255,255,255,0.1)"
          }}>
            <h3 style={{ margin: 0, color: "var(--text-primary)" }}>PDF Compartido</h3>
          </div>
          <div style={{
            flex: 1,
            display: "flex",
            alignItems: "center",
            justifyContent: "center",
            padding: "20px",
            background: "#2a2a2a"
          }}>
            <iframe
              src={`${API_URL}/pdf/file/${pdfState.filename}#page=${pdfState.currentPage}`}
              style={{ width: "100%", height: "100%", border: "none" }}
              title="PDF Viewer"
            />
          </div>
          <div style={{
            padding: "15px",
            borderTop: "1px solid rgba(255,255,255,0.1)",
            display: "flex",
            justifyContent: "space-between",
            alignItems: "center"
          }}>
            <button
              onClick={() => changePdfPage(-1)}
              disabled={pdfState.currentPage === 1}
              className="btn"
              style={{ padding: "8px 16px" }}
            >
              â† Anterior
            </button>
            <span style={{ color: "var(--text-primary)" }}>
              {pdfState.currentPage} / {pdfState.totalPages}
            </span>
            <button
              onClick={() => changePdfPage(1)}
              disabled={pdfState.currentPage === pdfState.totalPages}
              className="btn"
              style={{ padding: "8px 16px" }}
            >
              Siguiente â†’
            </button>
          </div>
        </div>
      )}
    </div>
  );
}

function VideoCard({ peer, peerID, isActive }) {
  const ref = useRef();
  const [hasVideo, setHasVideo] = useState(true);

  useEffect(() => {
    peer.on("stream", (stream) => {
      if (ref.current) {
        ref.current.srcObject = stream;
        // Check if stream has video
        const videoTracks = stream.getVideoTracks();
        setHasVideo(videoTracks.length > 0 && videoTracks[0].enabled);
      }
    });
  }, [peer]);

  return (
    <div style={{
      position: "relative",
      background: "#1a1a1a",
      borderRadius: "12px",
      overflow: "hidden",
      border: isActive ? "3px solid #10b981" : "3px solid transparent",
      transition: "all 0.3s ease",
      boxShadow: isActive ? "0 0 20px rgba(16, 185, 129, 0.5)" : "0 4px 6px rgba(0,0,0,0.3)",
      aspectRatio: "16/9",
      minHeight: "200px"
    }}>
      <video
        playsInline
        autoPlay
        ref={ref}
        style={{
          width: "100%",
          height: "100%",
          objectFit: "cover",
          display: hasVideo ? "block" : "none"
        }}
      />
      {!hasVideo && (
        <div style={{
          width: "100%",
          height: "100%",
          display: "flex",
          alignItems: "center",
          justifyContent: "center",
          background: "linear-gradient(135deg, #f093fb 0%, #f5576c 100%)"
        }}>
          <div style={{
            width: "80px",
            height: "80px",
            borderRadius: "50%",
            background: "rgba(255,255,255,0.2)",
            display: "flex",
            alignItems: "center",
            justifyContent: "center",
            fontSize: "2rem",
            color: "white",
            fontWeight: "bold"
          }}>
            ðŸ‘¤
          </div>
        </div>
      )}
      <div style={{
        position: "absolute",
        bottom: "10px",
        left: "10px",
        background: "rgba(0,0,0,0.8)",
        padding: "6px 12px",
        borderRadius: "6px",
        color: "white",
        fontSize: "0.9rem",
        fontWeight: "500"
      }}>
        Participante
      </div>
      {isActive && (
        <div style={{
          position: "absolute",
          top: "10px",
          right: "10px",
          background: "rgba(16, 185, 129, 0.9)",
          padding: "4px 8px",
          borderRadius: "4px",
          color: "white",
          fontSize: "0.75rem",
          fontWeight: "600"
        }}>
          ðŸŽ¤ Hablando
        </div>
      )}
    </div>
  );
}

