import React, { useEffect, useRef, useState } from "react";
import { useParams } from "react-router-dom";
import Peer from "simple-peer";
import io from "socket.io-client";

function Room() {
  const { roomId } = useParams();
  const [stream, setStream] = useState(null);
  const myVideo = useRef();
  const userVideo = useRef();
  const connectionRef = useRef();

  // Use window.location.hostname for socket connection too
  const host = window.location.hostname;
  // We need to keep a reference to socket to avoid re-creation but simplest is outside or memoized.
  // Putting it inside useEffect is tricky for cleanup. 
  // Let's create it once outside or useRef.
  const socketRef = useRef();

  useEffect(() => {
    socketRef.current = io.connect(`http://${host}:5000`);
    const socket = socketRef.current;

    // 1. Get User Media
    navigator.mediaDevices.getUserMedia({ video: true, audio: true })
      .then((currentStream) => {
        setStream(currentStream);
        if (myVideo.current) {
          myVideo.current.srcObject = currentStream;
        }

        // 2. Join the room via socket
        socket.emit("join-room", roomId);

        // 3. Setup Socket Listeners
        socket.on("user-joined", (userId) => {
          console.log("User joined:", userId);
          const peer = new Peer({
            initiator: true,
            trickle: false,
            stream: currentStream
          });

          peer.on("signal", (data) => {
            socket.emit("signal", { roomId, data });
          });

          peer.on("stream", (remoteStream) => {
            if (userVideo.current) {
              userVideo.current.srcObject = remoteStream;
            }
          });

          connectionRef.current = peer;
        });

        socket.on("signal", (data) => {
          if (connectionRef.current) {
            connectionRef.current.signal(data);
          } else {
            const peer = new Peer({
              initiator: false,
              trickle: false,
              stream: currentStream
            });

            peer.on("signal", (signalData) => {
              socket.emit("signal", { roomId, data: signalData });
            });

            peer.on("stream", (remoteStream) => {
              if (userVideo.current) {
                userVideo.current.srcObject = remoteStream;
              }
            });

            peer.signal(data);
            connectionRef.current = peer;
          }
        });
      })
      .catch(err => {
        console.error("Error accessing media devices:", err);
        alert("No pudimos acceder a tu cámara o micrófono.");
      });

    return () => {
      socket.disconnect();
      if (connectionRef.current) {
        connectionRef.current.destroy();
      }
    };
  }, [roomId, host]);

  return (
    <div className="fade-in" style={{ padding: "20px", display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
      <h2 style={{ marginBottom: '1.5rem', color: 'var(--text-secondary)' }}>
        Sala: <span style={{ color: 'var(--accent-primary)' }}>{roomId}</span>
      </h2>

      <div className="video-grid">
        <div className="video-container">
          <video playsInline muted ref={myVideo} autoPlay />
          <div className="video-label">Tú</div>
        </div>

        <div className="video-container">
          <video playsInline ref={userVideo} autoPlay />
          <div className="video-label">Remoto</div>
        </div>
      </div>

      <div style={{ marginTop: '20px', display: 'flex', gap: '10px' }}>
        <button className="btn" style={{ backgroundColor: 'var(--danger)' }} onClick={() => window.location.href = '/'}>
          Salir
        </button>
      </div>
    </div>
  );
}

export default Room;
